package com;

public class casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short r  = 20;
		int e = r;
		
		// casting implicito
		int t = 40000;
		short w = (short) t; // w puo assumero valori anomali 
		// posso castare anche variable float
		float tmp = 2.3F;
		double y = tmp;
		
	}

}
